import React, { useState, useEffect } from 'react';
import { DepartureTime } from './components/DepartureTime';
import './App.css';

const images = [
  '/static/images/train-station-1.jpg',
  '/static/images/train-station-2.jpg',
  '/static/images/train-station-3.jpg',
  '/static/images/train-station-4.jpg'
];

const App: React.FC = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [imageLoaded, setImageLoaded] = useState(false);

  const handleMouseEnter = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  useEffect(() => {
    // Preload images
    images.forEach(src => {
      const img = new Image();
      img.src = src;
      img.onload = () => setImageLoaded(true);
    });
  }, []);

  return (
    <div className="App">
      <header>
        <h1>Platform 1 Cafe</h1>
        <h2>Wandin Road, Camberwell, Victoria 3124</h2>
        <h3>City of Boroondara</h3>
        <div className="station-image" onMouseEnter={handleMouseEnter}>
          {images.map((image, index) => (
            <img
              key={image}
              src={image}
              alt={`Train station ${index + 1}`}
              className={index === currentImageIndex ? 'active' : ''}
            />
          ))}
        </div>
      </header>
      <main>
        <DepartureTime />
      </main>
      <footer>
        <div className="social-links">
          <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">Facebook</a>
          <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">Instagram</a>
        </div>
        <div className="copyright">
          made with ❤️ by john cox | {new Date().getFullYear()}
        </div>
      </footer>
    </div>
  );
}

export default App;
