import React, { useState, useEffect } from 'react';
import './DepartureTime.css';

interface Departure {
  scheduled_time: string;
  live_time?: string;
  destination: string;
}

interface ApiResponse {
  departures: Departure[];
  lastUpdated: string;
  updating: boolean;
}

const DepartureTime: React.FC = () => {
  const [currentTime, setCurrentTime] = useState<string>('');
  const [nextTrain, setNextTrain] = useState<{time: string, mins: number} | null>(null);
  const [departures, setDepartures] = useState<Departure[]>([]);
  const [lastUpdated, setLastUpdated] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  const fetchDepartures = async (refresh = false): Promise<void> => {
    try {
      setLoading(true);
      const response = await fetch(`http://localhost:5000/api/departures${refresh ? '?refresh=true' : ''}`);
      
      if (!response.ok) {
        throw new Error(`Server responded with ${response.status}`);
      }
      
      const data: ApiResponse = await response.json();
      setDepartures(data.departures);
      setLastUpdated(data.lastUpdated || '');
      setError(null);
    } catch (err) {
      setError(`Failed to fetch departures: ${err instanceof Error ? err.message : String(err)}`);
      console.error('Error fetching departures:', err);
    } finally {
      setLoading(false);
    }
  };

  const minutesUntil = (trainTime: string, currentTime: string): number => {
    const [trainHours, trainMins] = trainTime.split(':').map(Number);
    const [currentHours, currentMins] = currentTime.split(':').map(Number);
    const minutes = (trainHours * 60 + trainMins) - (currentHours * 60 + currentMins);
    return minutes < 0 ? minutes + (24 * 60) : minutes;
  };

  // Update current time and calculate next train
  useEffect(() => {
    const updateTimes = (): void => {
      const now = new Date();
      const melbourneTime = new Date(now.toLocaleString('en-US', { timeZone: 'Australia/Melbourne' }));
      const timeStr = melbourneTime.toLocaleTimeString('en-US', { 
        hour12: false,
        hour: '2-digit',
        minute: '2-digit'
      });
      setCurrentTime(timeStr);

      // Find next train from live API data
      if (departures.length > 0) {
        for (const departure of departures) {
          const departureTime = departure.scheduled_time;
          if (departureTime > timeStr) {
            setNextTrain({ 
              time: departureTime, 
              mins: minutesUntil(departureTime, timeStr) 
            });
            return;
          }
        }
        
        // If no trains found today, use the first train of tomorrow
        const firstTrain = departures[0];
        setNextTrain({ 
          time: firstTrain.scheduled_time, 
          mins: minutesUntil(firstTrain.scheduled_time, timeStr) 
        });
      }
    };

    updateTimes();
    const interval = setInterval(updateTimes, 60000);
    return () => clearInterval(interval);
  }, [departures]);

  // Fetch departures on component mount
  useEffect(() => {
    fetchDepartures();
    
    // Refresh data every 5 minutes
    const refreshInterval = setInterval(() => {
      fetchDepartures();
    }, 5 * 60 * 1000);
    
    return () => clearInterval(refreshInterval);
  }, []);

  return (
    <div className="departure-time">
      {loading && departures.length === 0 && (
        <div className="loading">Loading departure times...</div>
      )}
      
      {error && (
        <div className="error">
          {error}
          <button onClick={() => fetchDepartures(true)}>Retry</button>
        </div>
      )}
      
      {nextTrain && (
        <div className="next-train">
          <div className="train-time">Next train: {nextTrain.time}</div>
          <div className="minutes-until">
            Departing in: {
              nextTrain.mins >= 60
                ? `${Math.floor(nextTrain.mins / 60)} hour${Math.floor(nextTrain.mins / 60) > 1 ? 's' : ''} ${nextTrain.mins % 60} minute${nextTrain.mins % 60 !== 1 ? 's' : ''}`
                : `${nextTrain.mins} minute${nextTrain.mins !== 1 ? 's' : ''}`
           }
          </div>
        </div>
      )}
      
      {lastUpdated && (
        <div className="last-updated">
          Last updated: {lastUpdated}
          <button onClick={() => fetchDepartures(true)} disabled={loading}>
            {loading ? 'Refreshing...' : 'Refresh'}
          </button>
        </div>
      )}
      
      {departures.length > 0 && (
        <div className="all-departures">
          <h3>Today's Departures</h3>
          <div className="departures-list">
            {departures.slice(0, 5).map((dep: Departure, index: number) => (
              <div key={index} className="departure-item">
                <span className="departure-time">{dep.scheduled_time}</span>
                {dep.live_time && (
                  <span className="live-time">(Live: {dep.live_time})</span>
                )}
                <span className="destination">{dep.destination}</span>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default DepartureTime;
