import React, { useState, useEffect } from 'react';
import './DepartureTime.css';

interface Departure {
  scheduled_time: string;
  live_time: string | null;
  destination: string;
}

interface ApiResponse {
  departures: Departure[];
  lastUpdated: string;
  updating: boolean;
}

export const DepartureTime: React.FC = () => {
  const [departures, setDepartures] = useState<Departure[]>([]);
  const [lastUpdated, setLastUpdated] = useState<string>('');
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchDepartures = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/departures');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data: ApiResponse = await response.json();
      console.log('API Response:', data);
      setDepartures(data.departures);
      setLastUpdated(data.lastUpdated);
      setError(null);
    } catch (e) {
      const errorMessage = `Failed to fetch departures: ${e instanceof Error ? e.message : String(e)}`;
      console.error('Error details:', e);
      setError(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchDepartures();
    const interval = setInterval(fetchDepartures, 60000);
    return () => clearInterval(interval);
  }, []);

  if (isLoading) {
    return (
      <div className="departure-time">
        <div className="loading">Loading departure times...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="departure-time">
        <div className="error">{error}</div>
        <button onClick={fetchDepartures} className="retry-button">
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="departure-time">
      <div className="ptv-minimal">
        <h2>PTV but minimal cutetsy</h2>
        
        {/* Header Box with Time and Express */}
        <div className="header-box">
          <div>
            <div>10:21</div>
            <div className="express-label">LTD Express</div>
          </div>
          <div>Flinders Street</div>
        </div>

        {/* Scrollable Area */}
        <div className="scroll-area">
          {/* Muted/Faded scroll content */}
          <div style={{ opacity: 0.7 }}>
            Next departures will appear here...
          </div>
        </div>

        {/* Metro Grid */}
        <div className="metro-grid">
          <div className="metro-box platform">1</div>
          <div className="metro-box count">10</div>
          <div className="metro-box platform">2</div>
          <div className="metro-box count">27</div>
        </div>
      </div>

      <div className="last-updated">
        Last updated: {lastUpdated}
      </div>
    </div>
  );
};
