import React, { useState, useEffect } from 'react';
import './DepartureTime.css';

interface Departure {
  scheduled_time: string;
  live_time: string | null;
  destination: string;
}

interface ApiResponse {
  departures: Departure[];
  lastUpdated: string;
  updating: boolean;
}

export const DepartureTime: React.FC = () => {
  const [departures, setDepartures] = useState<Departure[]>([]);
  const [lastUpdated, setLastUpdated] = useState<string>('');
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchDepartures = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/departures');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data: ApiResponse = await response.json();
      setDepartures(data.departures);
      setLastUpdated(data.lastUpdated);
      setError(null);
    } catch (e) {
      const errorMessage = `Failed to fetch departures: ${e instanceof Error ? e.message : String(e)}`;
      console.error('Error details:', e);
      setError(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchDepartures();
    const interval = setInterval(fetchDepartures, 60000);
    return () => clearInterval(interval);
  }, []);

  const calculateMinutesUntil = (time: string): number => {
    const [hours, minutes] = time.split(':').map(Number);
    const now = new Date();
    const targetTime = new Date();
    targetTime.setHours(hours);
    targetTime.setMinutes(minutes);
    
    let diff = targetTime.getTime() - now.getTime();
    if (diff < 0) {
      diff += 24 * 60 * 60 * 1000; // Add 24 hours if time is tomorrow
    }
    return Math.floor(diff / 60000);
  };

  if (isLoading) {
    return (
      <div className="departure-time">
        <div className="loading">Loading departure times...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="departure-time">
        <div className="error">{error}</div>
        <button onClick={fetchDepartures} className="retry-button">
          Retry
        </button>
      </div>
    );
  }

  const nextDepartures = departures.slice(0, 4);

  return (
    <div className="departure-time floral-border">
      {/* Top Design */}
      <div className="finder-street-box">
        <h2>PTV Finders Street</h2>
        <div className="metro-counts">
          <div className="count-box">1</div>
          <div className="count-box">10</div>
          <div className="count-box">2</div>
          <div className="count-box">27</div>
        </div>
      </div>

      {/* Bottom Design */}
      <div className="minimal-timetable">
        <h3>PTV minimal cafe</h3>
        {nextDepartures.map((departure, index) => {
          const minutesUntil = calculateMinutesUntil(departure.scheduled_time);
          const isUrgent = minutesUntil <= 20;
          
          return (
            <div 
              key={index} 
              className={`time-block ${isUrgent ? 'urgent' : 'later'}`}
            >
              <div className="time">{departure.scheduled_time}</div>
              <div className="destination">{departure.destination}</div>
              <div className="minutes-label">{minutesUntil} min</div>
              {departure.live_time && (
                <span className="status-box live">LIVE</span>
              )}
            </div>
          );
        })}
      </div>

      <div className="last-updated">
        Last updated: {lastUpdated}
      </div>
    </div>
  );
};
